package com.lazycomedian.blog.annotation.impl;import com.lazycomedian.blog.annotation.validation.StatusValid;import com.lazycomedian.blog.enums.CommonStatusEnum;import javax.validation.ConstraintValidator;import javax.validation.ConstraintValidatorContext;import java.util.Arrays;import java.util.concurrent.atomic.AtomicBoolean;/** * @author lazyComedian * @date 2022/6/27 00:34 **/public class StatusValidator implements ConstraintValidator<StatusValid, Integer> {    @Override    public boolean isValid(Integer o, ConstraintValidatorContext constraintValidatorContext) {        AtomicBoolean flag = new AtomicBoolean(false);        Arrays.stream(CommonStatusEnum.values()).forEach(enums -> {            if (enums.getStatus().equals(o)) flag.set(true);        });        return flag.get();    }}