package com.lazycomedian.blog.annotation.validation;import com.lazycomedian.blog.annotation.impl.StatusValidator;import javax.validation.Constraint;import javax.validation.Payload;import java.lang.annotation.*;@Documented@Target({ElementType.PARAMETER, ElementType.FIELD})@Retention(RetentionPolicy.RUNTIME)@Constraint(validatedBy = StatusValidator.class)public @interface StatusValid {    String message() default "传入状态不合法";    Class<?>[] groups() default {};    Class<? extends Payload>[] payload() default {};}