package com.lazycomedian.blog.common.excption;import com.lazycomedian.blog.enums.StatusCodeEnum;import lombok.Getter;/** * 业务异常 * * @author lazyComedian * @date 2022/6/26 23:01 **/@Getterpublic class BizException extends RuntimeException {    /**     * 错误信息     */    private final String message;    /**     * 错误码     */    private Integer statusCode = StatusCodeEnum.FAILURE.getCode();    public BizException(String message) {        this.message = message;    }    public BizException(StatusCodeEnum statusCodeEnum) {        this.statusCode = statusCodeEnum.getCode();        this.message = statusCodeEnum.getDesc();    }    public BizException(Integer statusCode, String message) {        this.message = message;        this.statusCode = statusCode;    }}