package com.lazycomedian.blog.common.handler.auth;import com.alibaba.fastjson.JSON;import com.lazycomedian.blog.constants.CommonConst;import com.lazycomedian.blog.enums.StatusCodeEnum;import com.lazycomedian.blog.vo.response.Result;import org.springframework.security.core.AuthenticationException;import org.springframework.security.web.AuthenticationEntryPoint;import org.springframework.stereotype.Component;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;/** * 用户未登录时响应 * * @author lazyComedian * @date 2022/6/29 16:13 **/@Componentpublic class AuthenticationEntryPointImpl implements AuthenticationEntryPoint {    @Override    public void commence(HttpServletRequest request, HttpServletResponse response, AuthenticationException e) throws IOException {        response.setContentType(CommonConst.APPLICATION_JSON);        response.getWriter().write(JSON.toJSONString(Result.failure(StatusCodeEnum.UN_LOGIN)));    }}