package com.lazycomedian.blog.common.handler.auth;import com.alibaba.fastjson.JSON;import com.lazycomedian.blog.constants.CommonConst;import com.lazycomedian.blog.vo.response.Result;import org.springframework.security.core.AuthenticationException;import org.springframework.security.web.authentication.AuthenticationFailureHandler;import org.springframework.stereotype.Component;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;/** * 登录失败处理 * * @author lazyComedian * @date 2022/6/29 16:41 **/@Componentpublic class AuthenticationFailHandlerImpl implements AuthenticationFailureHandler {    @Override    public void onAuthenticationFailure(HttpServletRequest request, HttpServletResponse response, AuthenticationException e) throws IOException {        response.setContentType(CommonConst.APPLICATION_JSON);        response.getWriter().write(JSON.toJSONString(Result.failure(e.getMessage())));    }}