package com.lazycomedian.blog.common.handler.auth;import com.alibaba.fastjson.JSON;import com.lazycomedian.blog.vo.response.Result;import org.springframework.security.core.Authentication;import org.springframework.security.web.authentication.logout.LogoutSuccessHandler;import org.springframework.stereotype.Component;import javax.servlet.ServletException;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import static com.lazycomedian.blog.constants.CommonConst.APPLICATION_JSON;/** * 登出成功处理类 * * @author lazyComedian * @date 2022/6/30 09:55 **/@Componentpublic class LogoutSuccessHandlerImpl implements LogoutSuccessHandler {    @Override    public void onLogoutSuccess(HttpServletRequest request, HttpServletResponse response, Authentication e) throws IOException, ServletException {        response.setContentType(APPLICATION_JSON);        response.getWriter().write(JSON.toJSONString(Result.ok()));    }}