package com.lazycomedian.blog.config;import com.lazycomedian.blog.common.handler.auth.WebSecurityHandler;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.InterceptorRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;/** * web mvc配置 * * @author lazyComedian * @date 2022/7/1 09:46 **/@Configurationpublic class WebMvcConfig implements WebMvcConfigurer {    @Bean    public WebSecurityHandler getWebSecurityHandler() {        return new WebSecurityHandler();    }    @Override    public void addInterceptors(InterceptorRegistry registry) {        registry.addInterceptor(getWebSecurityHandler());    }}