package com.lazycomedian.blog.controller;import com.lazycomedian.blog.service.UserInfoService;import com.lazycomedian.blog.vo.response.Result;import com.lazycomedian.blog.vo.user.UserInfoVO;import com.lazycomedian.blog.vo.user.UserStatusVO;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;import javax.validation.Valid;/** * 用户信息 * * @author lazyComedian * @date 2022/6/26 21:45 **/@RestControllerpublic class UserInfoController {    @Autowired    private UserInfoService userInfoService;    @GetMapping("/users/test")    public Result<?> test() {        return Result.ok("测试数据");    }    /**     * 修改用户信息     * 备注     *     * @param userInfoVO 用户基本信息     */    @PutMapping("/users/info")    public Result<?> update(@Valid @RequestBody UserInfoVO userInfoVO) {        userInfoService.updateUserInfo(userInfoVO);        return Result.ok();    }    /**     * 修改用户状态     *     * @param userStatusVO 用户状态     */    @PutMapping("/admin/users/status")    public Result<?> updateStatus(@Valid @RequestBody UserStatusVO userStatusVO) {        userInfoService.updateUserStatus(userStatusVO);        return Result.ok();    }}