package com.lazycomedian.blog.dto;import com.lazycomedian.blog.enums.CommonStatusEnum;import lombok.Data;import org.springframework.security.core.GrantedAuthority;import org.springframework.security.core.userdetails.UserDetails;import java.util.Collection;/** * 用户信息DTO * * @author lazyComedian * @date 2022/6/29 17:08 **/@Datapublic class UserDetailDTO implements UserDetails {    /**     * 用户账号id     */    private Integer id;    /**     * 邮箱号     */    private String email;    /**     * 用户名     */    private String username;    /**     * 用户昵称     */    private String nickname;    /**     * 用户密码     */    private String password;    /**     * 用户头像     */    private String avatar;    /**     * 账户状态     */    private CommonStatusEnum status;    @Override    public Collection<? extends GrantedAuthority> getAuthorities() {        return null;    }    @Override    public String getPassword() {        return this.password;    }    @Override    public String getUsername() {        return this.username;    }    @Override    public boolean isAccountNonExpired() {        return false;    }    @Override    public boolean isAccountNonLocked() {        return this.status.getStatus() == 0;    }    @Override    public boolean isCredentialsNonExpired() {        return false;    }    @Override    public boolean isEnabled() {        return false;    }}