package com.lazycomedian.blog.dto.user;import com.lazycomedian.blog.enums.CommonStatusEnum;import lombok.Builder;import lombok.Data;import org.springframework.security.core.GrantedAuthority;import org.springframework.security.core.userdetails.UserDetails;import java.time.LocalDateTime;import java.util.Collection;import java.util.Objects;/** * 用户信息DTO * * @author lazyComedian * @date 2022/6/29 17:08 **/@Data@Builderpublic class UserDetailDTO implements UserDetails {    /**     * 用户账号id     */    private Integer id;    /**     * 用户信息id     */    private Integer userInfoId;    /**     * 邮箱号     */    private String email;    /**     * 用户名     */    private String username;    /**     * 用户昵称     */    private String nickname;    /**     * 用户密码     */    private String password;    /**     * 用户头像     */    private String avatar;    /**     * 用户简介     */    private String intro;    /**     * 账户状态     */    private Integer status;    /**     * 用户登录ip     */    private String ipAddress;    /**     * ip来源     */    private String ipSource;    /**     * 最近登录时间     */    private LocalDateTime lastLoginTime;    @Override    public Collection<? extends GrantedAuthority> getAuthorities() {        return null;    }    @Override    public String getPassword() {        return this.password;    }    @Override    public String getUsername() {        return this.username;    }    @Override    public boolean isAccountNonExpired() {        return true;    }    @Override    public boolean isAccountNonLocked() {        return Objects.equals(this.status, CommonStatusEnum.AVAILABLE.getStatus());    }    @Override    public boolean isCredentialsNonExpired() {        return true;    }    @Override    public boolean isEnabled() {        return true;    }}