package com.lazycomedian.blog.entity;import com.baomidou.mybatisplus.annotation.FieldFill;import com.baomidou.mybatisplus.annotation.TableField;import lombok.Data;import java.time.LocalDateTime;/** * 通用字段实体基类 * * @author lazyComedian * @date 2022/6/26 21:37 **/@Datapublic class GenericEntity {    /**     * 创建时间     */    @TableField(fill = FieldFill.INSERT)    protected LocalDateTime createTime;    /**     * 最后一次修改时间     */    @TableField(fill = FieldFill.UPDATE)    protected LocalDateTime updateTime;}