package com.lazycomedian.blog.entity;import com.baomidou.mybatisplus.annotation.*;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.time.LocalDateTime;/** * @author lazyComedian * @date 2022/7/1 10:16 **/@Data@Builder@AllArgsConstructor@NoArgsConstructor@TableName("role")public class RoleEntity {    /**     * 创建时间     */    @TableField(fill = FieldFill.INSERT)    private LocalDateTime createTime;    /**     * 最后一次修改时间     */    @TableField(fill = FieldFill.UPDATE)    private LocalDateTime updateTime;    /**     * 角色id     */    @TableId(value = "id", type = IdType.AUTO)    private Integer id;    /**     * 角色名     */    private String roleName;    /**     * 角色标签     */    private String roleLabel;    /**     * 状态 1可用 0禁用     */    private Integer status;}