package com.lazycomedian.blog.entity;import com.baomidou.mybatisplus.annotation.*;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.time.LocalDateTime;/** * 用户鉴权 * * @author lazyComedian * @date 2022/6/30 09:29 **/@NoArgsConstructor@AllArgsConstructor@Data@Builder@TableName("user_auth")public class UserAuthEntity {    /**     * 创建时间     */    @TableField(fill = FieldFill.INSERT)    private LocalDateTime createTime;    /**     * 最后一次修改时间     */    @TableField(fill = FieldFill.UPDATE)    private LocalDateTime updateTime;    /**     * id     */    @TableId(value = "id", type = IdType.AUTO)    private Integer id;    /**     * 用户信息id     */    private Integer userInfoId;        /**     * 用户名     */    private String username;    /**     * 密码     */    private String password;    /**     * 用户登录ip     */    private String ipAddress;    /**     * ip来源     */    private String ipSource;    /**     * 账号状态     */    private Integer status;    /**     * 最近登录时间     */    private LocalDateTime lastLoginTime;}