package com.lazycomedian.blog.entity;import com.baomidou.mybatisplus.annotation.*;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.time.LocalDateTime;/** * 用户信息 * * @author lazyComedian * @date 2022/6/26 21:34 **/@Data@NoArgsConstructor@AllArgsConstructor@Builder@TableName("user_info")public class UserInfoEntity {    /**     * 用户ID     */    @TableId(value = "id", type = IdType.AUTO)    private Integer id;    /**     * 创建时间     */    @TableField(fill = FieldFill.INSERT)    private LocalDateTime createTime;    /**     * 最后一次修改时间     */    @TableField(fill = FieldFill.UPDATE)    private LocalDateTime updateTime;    /**     * 邮箱号     */    private String email;    /**     * 用户昵称     */    private String nickname;    /**     * 用户头像     */    private String avatar;    /**     * 用户简介     */    private String intro;    /**     * 是否禁言     */    private Integer status;}