package com.lazycomedian.blog.entity;import com.baomidou.mybatisplus.annotation.IdType;import com.baomidou.mybatisplus.annotation.TableId;import com.baomidou.mybatisplus.annotation.TableName;import lombok.*;/** * 用户信息 * * @author lazyComedian * @date 2022/6/26 21:34 **/@Data@EqualsAndHashCode(callSuper = true)@NoArgsConstructor@AllArgsConstructor@Builder@TableName("user_info")public class UserInfoEntity extends GenericEntity {    /**     * 用户ID     */    @TableId(value = "id", type = IdType.AUTO)    protected Integer id;    /**     * 邮箱号     */    private String email;    /**     * 用户昵称     */    private String nickname;    /**     * 用户头像     */    private String avatar;    /**     * 用户简介     */    private String intro;    /**     * 是否禁言     */    private Integer status;}