package com.lazycomedian.blog.excption;import com.lazycomedian.blog.enums.StatusCodeEnum;import lombok.AllArgsConstructor;import lombok.Getter;/** * 业务异常 * * @author lazyComedian * @date 2022/6/26 23:01 **/@Getter@AllArgsConstructorpublic class ServiceException extends RuntimeException {    /**     * 错误信息     */    private final String message;    /**     * 错误码     */    private Integer statusCode = StatusCodeEnum.FAILURE.getCode();    public ServiceException(String message) {        this.message = message;    }    public ServiceException(StatusCodeEnum statusCodeEnum) {        this.statusCode = statusCodeEnum.getCode();        this.message = statusCodeEnum.getMsg();    }}