package com.lazycomedian.blog.handler;import com.baomidou.mybatisplus.core.handlers.MetaObjectHandler;import com.lazycomedian.blog.enums.ZoneEnum;import lombok.extern.slf4j.Slf4j;import org.apache.ibatis.reflection.MetaObject;import org.springframework.stereotype.Component;import java.time.LocalDateTime;import java.time.ZoneId;/** * mybatis plus 自动填充字段 * * @author lazyComedian * @date 2022/6/26 22:01 **/@Slf4j@Componentpublic class CommonMetaObjectHandler implements MetaObjectHandler {    @Override    public void insertFill(MetaObject metaObject) {        log.info("start insert fill ....");        this.strictInsertFill(metaObject, "createTime", LocalDateTime.class,                LocalDateTime.now(ZoneId.of(ZoneEnum.SHANGHAI.getZone()))        );    }    @Override    public void updateFill(MetaObject metaObject) {        log.info("start update fill ....");                this.strictUpdateFill(metaObject, "updateTime", LocalDateTime.class,                LocalDateTime.now(ZoneId.of(ZoneEnum.SHANGHAI.getZone())));    }}