package com.lazycomedian.blog.utils;import org.springframework.beans.BeanUtils;import java.util.ArrayList;import java.util.List;/** * 对象集合工具类 * * @author lazyComedian * @date 2022/6/30 09:44 **/public class BeanCopyUtils {    /**     * 拷贝对象     *     * @param source 源     * @param target 目标     */    public static <T> T assign(Object source, Class<T> target) {        T result = null;        try {            result = target.newInstance();            if (null != source) {                BeanUtils.copyProperties(source, result);            }        } catch (InstantiationException | IllegalAccessException ex) {            throw new RuntimeException(ex);        }        return result;    }    /**     * 拷贝集合     *     * @param source 源     * @param target 目标     */    public static <T, S> List<T> copyList(List<S> source, Class<T> target) {        List<T> list = new ArrayList<>();        if (null != source && source.size() > 0) {            for (Object obj : source) {                list.add(BeanCopyUtils.assign(obj, target));            }        }        return list;    }}