package com.lazycomedian.blog.utils;import com.lazycomedian.blog.dto.UserDetailDTO;import org.springframework.security.core.context.SecurityContextHolder;/** * 用户工具类 * * @author lazyComedian * @date 2022/6/29 17:06 **/public class UserUtils {    /**     * 获取当前登录用户     *     * @return 用户登录信息     */    public static UserDetailDTO getLoginUser() {        return (UserDetailDTO) SecurityContextHolder.getContext().getAuthentication().getPrincipal();    }}