package com.lazycomedian.blog.vo.response;import com.lazycomedian.blog.enums.StatusCodeEnum;import lombok.AllArgsConstructor;import lombok.Getter;import java.io.Serializable;/** * 统一接口返回数据格式 * * @author lazyComedian * @date 2022/6/26 20:09 **/@Getter@AllArgsConstructorpublic class Result<T> implements Serializable {    /**     * 状态码     */    private final Integer statusCode;    /**     * 提示信息     */    private final String message;    /**     * 返回数据     */    private final T data;    /**     * 请求成功返回     *     * @author lazyComedian     * @date 2022/6/18 15:41     **/    public static <T> Result<T> ok() {        return new Result<>(StatusCodeEnum.OK.getCode(), StatusCodeEnum.OK.getMsg(), null);    }    public static <T> Result<T> ok(T data) {        return new Result<>(StatusCodeEnum.OK.getCode(), StatusCodeEnum.OK.getMsg(), data);    }    /**     * 请求失败返回     *     * @author lazyComedian     * @date 2022/6/18 15:39     **/    public static <T> Result<T> failure() {        return new Result<>(StatusCodeEnum.FAILURE.getCode(), StatusCodeEnum.FAILURE.getMsg(), null);    }    public static <T> Result<T> failure(String msg) {        return new Result<>(StatusCodeEnum.FAILURE.getCode(), msg, null);    }    public static <T> Result<T> failure(String msg, T data) {        return new Result<>(StatusCodeEnum.FAILURE.getCode(), msg, data);    }    public static <T> Result<T> failure(StatusCodeEnum statusCode) {        return new Result<>(statusCode.getCode(), statusCode.getMsg(), null);    }    public static <T> Result<T> failure(StatusCodeEnum code, T data) {        return new Result<>(code.getCode(), code.getMsg(), data);    }    public static <T> Result<T> failure(Integer code, String msg) {        return new Result<>(code, msg, null);    }}